import React from 'react'

export class Canvas extends React.Component {

	constructor() {
		super()
	}

	componentDidMount() {
		this.setupCanvas()
	}

	componentDidUpdate() {
		this.setupCanvas()
	}

	setupCanvas() {
		let ctx = this.refs.canvas.getContext('2d')

		let penDown = false

		startDrawing = () => {
			penDown = true
		}

		endDrawing = () => {
			penDown = false
			ctx.beginPath()
		}

		draw = e => {
			if (!penDown) 
				return

			ctx.lineWidth = 5
			ctx.lineCap = 'round'

			ctx.lineTo(e.clientX, e.clientY)
			ctx.stroke()

		}

		this.refs.canvas.addEventListener('mousedown', startDrawing)
		this.refs.canvas.addEventListener('mouseup', endDrawing)
		this.refs.canvas.addEventListener('mousemove', draw)

	}

	render() {

		var canvasStyle = {
			"border": "2px solid black",
			"marginTop": "100pt",
			"marginLeft": "10%",
			"marginRight": "90%",
			"width": "500pt",
			"height": "350pt"
		}

		return (<div>
			<canvas ref="canvas" style={canvasStyle}>
				Sorry your browser does not support canvas
			</canvas>
		</div>)
	}

}
