import React from 'react'

export class Canvas extends React.Component {

	componentWillMount() {
		let canvas = this.refs.canvas
		canvas.setAttribute('width', this.refs.canvas.parentNode.offsetWidth)
		canvas.setAttribute('height', this.refs.canvas.parentNode.offsetHeight)
	}

	componentDidMount() {

		// const {canvasHeight, canvasWidth} = this.state.canvasDims
		// this.refs.canvas.height = canvasHeight
		// this.refs.canvas.width = canvasWidth
		this.setupCanvas()
	}

	componentDidUpdate() {
		// const {canvasHeight, canvasWidth} = this.state.canvasDims
		// this.refs.canvas.height = canvasHeight
		// this.refs.canvas.width = canvasWidth

		this.setupCanvas()
	}

	setupCanvas() {
		const ctx = this.refs.canvas.getContext('2d')
		ctx.fillRect(50, 50, 20, 20)

		let penDown = false

		let startDrawing = () => {
			penDown = true
		}

		let endDrawing = () => {
			penDown = false
			ctx.beginPath()
		}

		let draw = e => {

			console.log(e.clientX, e.clientY)
			// if (!penDown)
			// 	return

			// ctx.lineWidth = 5
			// ctx.lineCap = 'round'
			ctx.lineTo(e.clientX, e.clientY);
			ctx.stroke()

		}

		this.refs.canvas.addEventListener('mousedown', startDrawing)
		this.refs.canvas.addEventListener('mouseup', endDrawing)
		this.refs.canvas.addEventListener('mousemove', draw)

	}

	render() {

		var canvasStyle = {
			"border": "2px solid black",
			"marginTop": "10%	",
			"marginLeft": "10%",
			"marginRight": "90%"
		}

		return (<div>
			<canvas ref="canvas" style={canvasStyle}>
				Sorry your browser does not support canvas
			</canvas>
		</div>)
	}

}
