import React from 'react'

export class Canvas extends React.Component {

	constructor() {
		super();
	}

	componentDidMount() {
		this.setupCanvas()
	}

	componentDidUpdate() {
		this.setupCanvas()
	}

	setupCanvas() {}

	render() {

		var canvasStyle = {
			"border": "2px solid black",
			"marginTop": "100pt",
			"marginLeft": "10%",
			"marginRight": "90%",
			"width": "500pt",
			"height": "350pt"
		}

		return (<div>
			<canvas ref="drawHere" style={canvasStyle}>
				Sorry your browser does not support canvas
			</canvas>
		</div>)
	}

}
